const sliderWrapper=document.querySelector(".slider__wrapper"),slider=document.querySelector(".slider");let mySwiper=new Swiper(sliderWrapper,{slidesPerView:1,spaceBetween:500,loop:!0,slideClass:"slider__item",wrapperClass:"slider__list",pagination:{el:".slider__pagination",clickable:!0},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});const sliderButtonNext=document.querySelector(".slider__button-next"),sliderButtonPrev=document.querySelector(".slider__button-prev"),sliderBackground=document.querySelector(".slider--background"),additionalColor=document.querySelector(".slider--additional-color");sliderButtonNext.addEventListener("click",(()=>{const e=document.querySelector(".swiper-slide-active").dataset.color;window.matchMedia("(min-width: 768px) and (max-width: 1439px)").matches||(sliderBackground.style.backgroundColor=e),additionalColor.style.setProperty("--color",e)})),sliderButtonPrev.addEventListener("click",(()=>{const e=document.querySelector(".swiper-slide-active").dataset.color;window.matchMedia("(min-width: 768px) and (max-width: 1439px)").matches||(sliderBackground.style.backgroundColor=e),additionalColor.style.setProperty("--color",e)})),window.addEventListener("resize",(()=>{const e=window.innerWidth,t=document.querySelector(".swiper-slide-active").dataset.color;sliderBackground.style.backgroundColor=e>=768&&e<=1439?"":t})),document.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelector(".swiper-slide-active").dataset.color;additionalColor.style.setProperty("--color",e)}));